mapper.insert:
insert into daily_sales (
worker_id , worker_name  , sale_amount , profile, record_date , create_time , update_time, client_name, is_new_client, remark
) values (
#{workerId} , #{workerName}, #{saleAmount} , #{profile}, #{recordDate} , #{createTime} , #{updateTime}, #{clientName}, #{isNewClient}, #{remark}
);

mapper.update:
update daily_sales set
worker_id = #{workerId} , worker_name = #{workerName} , sale_amount = #{saleAmount} , profile = #{profile} ,
record_date = #{recordDate} , create_time = #{createTime} , update_time = #{updateTime} , client_name = #{clientName},
is_new_client = #{isNewClient} , remark = #{remark}
where id = #{id};

mapper.select:
select worker_id workerId,worker_name workerName,SUM(`profile`) profile,SUM(`sale_amount`) saleAmount
from daily_sales

GROUP BY worker_id order by SUM(`profile`) desc;
