mapper.sql:
delete_flag = 0

    and worker_name = #{workerName}


     and record_date >=  #{startTime} 


     and record_date <= #{endTime}

mapper.insert:
insert into daily_sales (
worker_id , worker_name  , city_id, sale_amount , profile, record_date , create_time , update_time, client_name, is_new_client, remark, delete_flag
) values (
#{workerId} , #{workerName}, #{cityId}, #{saleAmount} , #{profile}, #{recordDate} , #{createTime} , #{updateTime}, #{clientName}, #{isNewClient}, #{remark}, #{deleteFlag}
);

mapper.update:
update daily_sales set
worker_id = #{workerId} , worker_name = #{workerName} , city_id = #{cityId} , sale_amount = #{saleAmount} , profile = #{profile} ,
record_date = #{recordDate} , create_time = #{createTime} , update_time = #{updateTime} , client_name = #{clientName},
is_new_client = #{isNewClient} , remark = #{remark}
where id = #{id};

sql.where:
delete_flag = 0

     and record_date >=  #{startTime} 


     and record_date <= #{endTime} 


    and worker_name = #{workerName}


    and to_days(record_date) = to_days(now())


    and DATE_FORMAT(record_date, '%Y%m') = DATE_FORMAT(CURDATE()'%Y%m')


    and city_id = #{cityId}

mapper.select:
select worker_id workerId,worker_name workerName,SUM(`profile`) profile,SUM(`sale_amount`) saleAmount
from daily_sales

GROUP BY worker_id order by SUM(`sale_amount`) desc;

select worker_id workerId , worker_name workerName , SUM(`profile`) profile , SUM(`sale_amount`) saleAmount
from daily_sales
where record_date >= #{startTime} and record_date <= #{endTime}

    and city_id = #{cityId}

and delete_flag = 0
group by worker_id order by saleAmount desc;
