mapper.insert:
insert into worker (
id , real_name , sys_name , wx_card , sex , age , work_age , city_code , city_name , create_time , update_time , delete_flag
) values (
#{id} , #{realName} , #{sysName} , #{wxCard} , #{sex} , #{age} , #{workAge} , #{cityCode} , #{cityName} , #{createTime} , #{updateTime} , #{deleteFlag}
);

mapper.update:
update worker set
real_name = #{realName} , sys_name = #{sysName} , wx_card = #{wxCard} , sex = #{sex} , age = #{age} ,
work_age = #{workAge} , city_code = #{cityCode} , city_name = #{cityName} , update_time = #{updateTime} , delete_flag = #{deleteFlag}
where id = #{id};
